import { useCallback } from "react";
import { useReauthenticate } from "./useReauthenticate.js";
const useSignUp = (authImplementation, setAuthProviderState, getCurrentUser, skipFetchCurrentUser) => {
  const reauthenticate = useReauthenticate(
    authImplementation,
    setAuthProviderState,
    getCurrentUser,
    skipFetchCurrentUser
  );
  return useCallback(
    async (options) => {
      const signupOutput = await authImplementation.signup(options);
      await reauthenticate();
      return signupOutput;
    },
    [authImplementation, reauthenticate]
  );
};
export {
  useSignUp
};
